<script>
    document.addEventListener('alpine:init', () => {
    Alpine.data('floatingButtons', () => ({
        visible: false,
        scrollYProgress: 0,
        lastBodyTop: 0,
        lastDirection: null,

        init() {
            this.checkCookies();
            this.handleCookiePrefs.bind(this);
            this.lastBodyTop = document.body.getBoundingClientRect().top;
            this.handleScroll();

            const throttledScroll = this.throttle(this.handleScroll.bind(this), 100);
            window.addEventListener('scroll', throttledScroll);

            window.addEventListener('cc:onConsent', () => {
                const btn = document.getElementById('cookie_but');
                if (btn) {
                    btn.classList.add('flex');
                    btn.classList.remove('hidden');
                }
            });
        },

        handleScroll() {
            const scrollTop = window.scrollY || document.documentElement.scrollTop;
            const docHeight = document.documentElement.scrollHeight - window.innerHeight;
            this.scrollYProgress = docHeight > 0 ? scrollTop / docHeight : 0;

            const currentBodyTop = document.body.getBoundingClientRect().top;
            const delta = currentBodyTop - this.lastBodyTop;

            if (Math.abs(delta) > 10) {
                const direction = delta > 0 ? 'up' : 'down';

                if (direction !== this.lastDirection) {
                    this.lastDirection = direction;
                }
            }

            this.lastBodyTop = currentBodyTop;

            if (this.scrollYProgress < 0.05) {
                this.visible = false;
            } else {
                this.visible = (this.lastDirection === 'up');
            }

        },

        throttle(func, limit) {
            let lastFunc;
            let lastRan;
            return function(...args) {
                const context = this;
                if (!lastRan) {
                    func.apply(context, args);
                    lastRan = Date.now();
                } else {
                    clearTimeout(lastFunc);
                    lastFunc = setTimeout(function() {
                        if ((Date.now() - lastRan) >= limit) {
                            func.apply(context, args);
                            lastRan = Date.now();
                        }
                    }, limit - (Date.now() - lastRan));
                }
            };
        },

        scrollToTop() {
            window.scrollTo({
                top: 0,
                behavior: 'smooth'
            });
        },

        handleCookiePrefs(e) {
            e.preventDefault();
            e.stopPropagation();
            const domEvent = new Event('click');
            window.CookieConsent?.showPreferences(domEvent);
        },

        checkCookies() {
            const cookieName = 'naaba_cookie';
            const btn = document.getElementById('cookie_but');
            const cookie = document.cookie.match(
                '(^|;)\\s*' + cookieName + '\\s*=\\s*([^;]+)'
            );

            cookie ? (btn?.classList.add('flex'), btn?.classList.remove('hidden')) : (btn?.classList.remove('flex'), btn?.classList.add('hidden'));
        },
        acceptCookies() {
            window.CookieConsent?.acceptAll();
            const btn = document.getElementById('cookie_but');
            if (btn) {
                btn.classList.add('hidden');
                btn.classList.remove('flex');
            }
        }
    }));
});
</script>
